CREATE TABLE if not exists `mc_works` (`ID` int NOT NULL, `CLIENT` int NOT NULL, `SPEC` int NOT NULL, `WORK_ID` int NOT NULL, `STATUS` varchar(8) NOT NULL, `WORK_EVENT_ID` int NOT NULL, `TEST_EVENT_ID` int NOT NULL, `CARD_TYPE` varchar(5) NOT NULL, `DATA` varchar(255) NOT NULL, `SPEC_REPORT` varchar(255) NOT NULL, PRIMARY KEY(`ID`));
CREATE TABLE if not exists `mc_events` (`ID` int NOT NULL AUTO_INCREMENT, `START_DATE` datetime NOT NULL, `END_DATE` datetime NOT NULL, `TEXT` varchar(255) NOT NULL, `COLOR` varchar(6) NOT NULL, `EVENT_TYPE` varchar(7) NOT NULL, `DETAILS` varchar(255) NOT NULL, `REC_TYPE` text NOT NULL, `EVENT_LENGHT` int NOT NULL, `EVENT_PID` int NOT NULL, `LINK_ID` int NOT NULL, `SECURE_ID` int NOT NULL, PRIMARY KEY(`ID`));
CREATE TABLE if not exists `mc_config` (`ID` int NOT NULL, `VALUE` varchar(255) NOT NULL, PRIMARY KEY(`ID`));